"use strict"

const sendPlace = require("send-place")

module.exports = {
  "id": "cinema",
  "title": "May the Force be with You",
  "description": "Nous sommes le 16 décembre 2015. Le film s'apprête à commencer. Quelle meilleure récompense ?",
  "handler": handler,
  "links": [
    {
      "description": "Regarder le trailer",
      "href": "!https://www.youtube.com/watch?v=sGbxmsDFVnE",
      "rel": ["awesome", "external"]
    }
  ]
}

function handler(place, request, response) {
  response.status(200).set("Date", "Wed, 16 Dec 2015 18:45:00 GMT")
  sendPlace(response, place)
}
