"use strict"

const _ = require("lodash")
const {generate: generateId} = require("shortid")

const gameRoutes = {
  landing: "/landing"
}

module.exports = {
  routes: {
    "space": "/"
  },
  newGameRouter() {
    return {
      routes: _.clone(gameRoutes),
      rootUri: "/" + generateId(),
      link(resourceId) {
        return this.rootUri + this.routes[resourceId]
      }
    }
  }
}


